import {
  b2Body,
  b2BodyDef,
  b2BodyType,
  b2PolygonShape,
  b2ShapeType,
  b2World,
} from "box2d.ts";
import { World } from "flat-ecs";
import {
  AssetManager,
  Color,
  InputEvent,
  InputHandler,
  PolygonBatch,
  Screen,
  ShapeRenderer,
  Vector2,
  Viewport,
  ViewportInputHandler,
} from "gdxts";
import { Constants } from "../Constant";

export const createGameScreen = async (
  assetManager: AssetManager,
  viewport: Viewport
): Promise<Screen> => {
  const createGround = (
    physicWorld: b2World,
    x: number,
    y: number,
    width: number,
    height: number
  ) => {
    const bodyDef = new b2BodyDef();
    bodyDef.type = b2BodyType.b2_staticBody;
    bodyDef.position.Set(x + width / 2, y + height / 2);
    const body = physicWorld.CreateBody(bodyDef);
    const shape = new b2PolygonShape();
    shape.SetAsBox(width / 2, height / 2);
    body.SetUserData("ground");
    body.CreateFixture(shape);
  };

  const createPerson = (
    physicWorld: b2World,
    x: number,
    y: number,
    width: number,
    height: number
  ): b2Body => {
    const bodyDef = new b2BodyDef();
    bodyDef.type = b2BodyType.b2_dynamicBody;
    bodyDef.position.Set(x + width / 2, y + height / 2);
    const body = physicWorld.CreateBody(bodyDef);
    body.SetUserData("person");
    return body;
  };

  const gl = viewport.getContext();
  const camera = viewport.getCamera();
  const batch = new PolygonBatch(gl);
  const shapeRenderer = new ShapeRenderer(gl);
  camera.setYDown(true);
  batch.setYDown(true);
  gl.clearColor(0, 0, 0, 1);

  const world = new World();
  const physicWorld = new b2World({
    x: 0,
    y: 100,
  });
  const mapData = await fetch("./datas.tmj").then((res) => res.json());
  const groundData = mapData.layers.find(
    (w: any) => w.name === "Ground"
  ).objects;
  const personTeam1Data = mapData.layers.find(
    (p: any) => p.name === "Team1"
  ).objects;
  const personTeam2Data = mapData.layers.find(
    (p: any) => p.name === "Team2"
  ).objects;
  let Team1: b2Body[] = [];
  let Team2: b2Body[] = [];

  for (let ground of groundData) {
    console.log(ground);
    createGround(
      physicWorld,
      ground.x / Constants.METER_TO_PHYSIC_WORLD,
      ground.y / Constants.METER_TO_PHYSIC_WORLD,
      ground.width / Constants.METER_TO_PHYSIC_WORLD,
      ground.height / Constants.METER_TO_PHYSIC_WORLD
    );
  }
  // for (let person1 of personTeam1Data) {
  //   Team1.push(
  //     createPerson(
  //       physicWorld,
  //       person1.x / Constants.METER_TO_PHYSIC_WORLD,
  //       person1.y / Constants.METER_TO_PHYSIC_WORLD,
  //       person1.width / Constants.METER_TO_PHYSIC_WORLD,
  //       person1.height / Constants.METER_TO_PHYSIC_WORLD
  //     )
  //   );
  // }
  // for (let person2 of personTeam2Data) {
  //   Team2.push(
  //     createPerson(
  //       physicWorld,
  //       person2.x / Constants.METER_TO_PHYSIC_WORLD,
  //       person2.y / Constants.METER_TO_PHYSIC_WORLD,
  //       person2.width / Constants.METER_TO_PHYSIC_WORLD,
  //       person2.height / Constants.METER_TO_PHYSIC_WORLD
  //     )
  //   );
  // }
  console.log(personTeam1Data);
  const inputHandle = new ViewportInputHandler(viewport);
  // inputHandle.addEventListener(InputEvent.TouchStart, () => {
  //   Team1[0].ApplyLinearImpulseToCenter(
  //     {
  //       x: 5,
  //       y: 5,
  //     },
  //     true
  //   );
  // });

  world.register("gl", gl);
  world.register("assetManager", assetManager);
  world.register("viewport", viewport);
  world.register("batch", batch);
  world.register("physicWorld", physicWorld);

  const vertices: number[] = [];
  return {
    update(delta: number) {
      gl.clear(gl.COLOR_BUFFER_BIT);
      batch.setProjection(camera.combined);
      shapeRenderer.setProjection(camera.combined);
      world.setDelta(delta);
      world.processActiveSystem();
      world.processPassiveSystem();
      physicWorld.Step(delta, 8, 3);
      shapeRenderer.begin();

      shapeRenderer.rect(
        true,
        0,
        0,
        Constants.WORLD_WIDTH,
        Constants.WORLD_HEIGHT
      );

      let body = physicWorld.GetBodyList();
      while (body) {
        const fixture = body.GetFixtureList();
        if (fixture) {
          const type = fixture.GetType();
          if (type === b2ShapeType.e_polygonShape) {
            const data = fixture.GetShape() as b2PolygonShape;
            vertices.length = 0;
            for (let i = 0; i < data.m_vertices.length; i++) {
              vertices.push(data.m_vertices[i].x);
              vertices.push(data.m_vertices[i].y);
            }
            shapeRenderer.polygon(vertices, vertices.length, 0, Color.BLUE);
          }
        }
        body = body.GetNext();
      }
      for (let ground of groundData) {
        shapeRenderer.rect(
          true,
          ground.x * Constants.METER_TO_PHYSIC_WORLD,
          ground.y * Constants.METER_TO_PHYSIC_WORLD,
          ground.width * Constants.METER_TO_PHYSIC_WORLD,
          ground.height * Constants.METER_TO_PHYSIC_WORLD,
          Color.BLUE
        );

        // for (let i = 0; i < Team1.length; i++) {
        //   shapeRenderer.rect(
        //     true,
        //     Team1[i].GetPosition().x * Constants.METER_TO_PHYSIC_WORLD,
        //     Team1[i].GetPosition().y * Constants.METER_TO_PHYSIC_WORLD,
        //     Constants.PERSON_WIDTH * Constants.METER_TO_PHYSIC_WORLD,
        //     Constants.PERSON_HEIGHT * Constants.METER_TO_PHYSIC_WORLD,
        //     Color.RED
        //   );
        // }

        // for (let person2 of personTeam2Data) {
        //   shapeRenderer.rect(
        //     true,
        //     person2.x * Constants.METER_TO_PHYSIC_WORLD,
        //     person2.y * Constants.METER_TO_PHYSIC_WORLD,
        //     Constants.PERSON_WIDTH * Constants.METER_TO_PHYSIC_WORLD,
        //     Constants.PERSON_HEIGHT * Constants.METER_TO_PHYSIC_WORLD,
        //     Color.RED
        //   );
        // }
      }
      shapeRenderer.end();
    },
    dispose(): void {},
  };
};
