import { b2Body } from "box2d.ts";
import { System, Inject } from "flat-ecs";
import { Color, ShapeRenderer } from "gdxts";
import { Constants } from "../Constant";

export class RenderSystem extends System {
  @Inject("shapeRenderer") shapeRenderer: ShapeRenderer;
  @Inject("Team1") Team1: b2Body[];
  @Inject("Team2") Team2: b2Body[];
  @Inject("groundData") groundData: any;
  @Inject("balls") balls: b2Body[];

  process(): void {
    this.shapeRenderer.begin();
    for (let ground of this.groundData) {
      this.shapeRenderer.rect(
        true,
        ground.x,
        Constants.WORLD_HEIGHT - ground.y - 100,
        ground.width,
        ground.height,
        Color.GREEN
      );
    }
    for (let player of this.Team1) {
      this.shapeRenderer.rect(
        true,
        player.GetPosition().x * Constants.METER_TO_PHYSIC_WORLD,
        player.GetPosition().y * Constants.METER_TO_PHYSIC_WORLD - 150,
        Constants.PERSON_WIDTH,
        Constants.PERSON_HEIGHT
      );
    }
    for (let player of this.Team2) {
      this.shapeRenderer.rect(
        true,
        player.GetPosition().x * Constants.METER_TO_PHYSIC_WORLD,
        player.GetPosition().y * Constants.METER_TO_PHYSIC_WORLD - 150,
        Constants.PERSON_WIDTH,
        Constants.PERSON_HEIGHT
      );
    }
    for (let ball of this.balls) {
      this.shapeRenderer.circle(
        true,
        ball.GetPosition().x * Constants.METER_TO_PHYSIC_WORLD,
        ball.GetPosition().y * Constants.METER_TO_PHYSIC_WORLD - 150,
        0.5
      );
    }

    this.shapeRenderer.end();
  }
}
